import os,random,sys
import urllib.parse
import glob
from mutagen.mp3 import MP3
from mutagen.id3 import ID3
# the folder, where app/apk can save data (parent folder)
FOLDER=os.path.join( os.path.dirname(os.path.abspath(os.path.realpath(sys.argv[0]))), ".." )
EXTS=[".mp3",".ogg"]
VINYL="data:image/png;base64,"
PLAYING = "data:image/gif;base64,"

# TODO:
# https://developer.chrome.com/blog/autoplay/

def img2dataurl(img):
    data = io.BytesIO()
    img = img.convert('RGBA')
    img.save(data, "PNG")
    data64 = base64.b64encode(data.getvalue())
    return u'data:image/png;base64,'+data64.decode()   

def getimage(path):
    try:
        tags = ID3(path)
        #~ print("ID3 tags included in this song ------------------")
        #~ print(tags.pprint())
        #~ print("-------------------------------------------------")
        pict = tags.get("APIC:").data
        return Image.open(io.BytesIO(pict))
    except Exception as e:
        print("ERROR TAG",path,"--->",e)
        return None

class LocalPlayer(Tag.div):
    statics="""
    .cover {width:200px;height:200px;border:2px solid red;cursor:pointer}
    """
    def init(self,ll:list,volume:float,cbchange):
        self["style"]={"background":"#EEE","width":"100%","height":"100%"}
        self._ll=ll
        self._cbchange=cbchange
        self._volume=volume
        self._playing = Tag.img(_src=PLAYING,_style="display:none")
        self._player=Tag.AUDIO(_controls=True,_autoplay=True,_type="audio/mpeg",_style={"width":"100%","font-size":"2em","background":"#EEE","pointer-events": "none"})
        self._player["onended"] = self.bind.next()
        self._player["onplaying"] = self.bind.set_status(1)
        self._player["onpause"] = self.bind.set_status(0)

        self._img = Tag.img(_class="cover",_onclick=self.bind(self.play_pause),_title="play/pause",_style="flex: 0 0 60px;")
        self._ovol=Tag.span()
        
        self._title=Tag.div("title",_style="flex: 0 0 auto;width:100%;")
        self._btns=Tag.span("btns")
        self <= self._player
        
        self <= Tag.div( self._img + self._title, _class="main")
        
        self.change_volume()
        
        self += Tag.button("V-",_onclick=lambda o: self.change_volume(-0.05),_class="bigbtn");
        self += Tag.button("V+",_onclick=lambda o: self.change_volume(+0.05),_class="bigbtn");
        self += self._btns
        self += self._ovol
        self += Tag.button("📂",_onclick=lambda o: self.root.action("ACTION_FOLDER"),_class="bigbtn topright");

        self.play( self._ll.pop(0) )

    def set_status(self,b):
        if b:
            self._playing["style"]="display:inline-block"
        else:
            self._playing["style"]="display:none"

    def play_pause(self):
        self._player.call( "if(self.paused) self.play(); else self.pause(); beep()" )

    def change_volume(self,off=0):
        self._volume+=off
        if self._volume<0:
            self._volume=0.0
        elif self._volume>1:
            self._volume=1.0
        self._ovol.set("%s%%" % int(self._volume * 100))
        if off!=0:
            self.call(f"beep();document.getElementById('{id(self._player)}').volume={round(self._volume,2)}")

        self._cbchange( self )
            
    def play(self,path:str):
        aa=Tag.span( os.path.basename(path) ,_style="font-size:2em")
            
        self._title.set( [self._playing, aa] )
        self._btns.clear()
        if self._ll:
            self._btns <= Tag.Button("&#9654;&#9654;",_onclick=self.bind(self.next)+"beep()",_class="bigbtn")
            self._btns+="(%s)"%len(self._ll)
            
        self._player["src"]="/get/"+urllib.parse.quote(path)
        self._player["onloadstart"] = f"this.volume={self._volume};this.play()"

        im = getimage(path)
        if im:
            self._img["src"] = img2dataurl(im)
        else:
            self._img["src"]= VINYL
   
    def next(self,o=None):
        if self._ll:
            self.play( self._ll.pop(0) )
            self._cbchange( self )
        else:
            self._player.call( "self.pause()" )


class Paths(Tag.div):
    statics="""
    .file,.folder {margin-left:8px;cursor:pointer;overflow-x:hidden;border-bottom:1px dotted #AAA;}
    .file:hover, .folder:hover {background:#EEE}
    .file *,.folder * {text-decoration: none; overflow-x:hidden}
    .folder * {font-weight:800}
    """
    imports=[]

    def init(self,cb,path="/",**a):
        self["style"]="width:100%;overflow-x:hidden;"
        self.cb=cb
        self.omain = Tag.div()
        
        self+= self.omain
        
        if not self.list(path):
            self+=Tag.span(f"cant ({path});-(")
            self+=Tag.span(f"Add android rights to MAAR !")

    def list(self,path):
        self.js="window.scrollTo(0,0);"
        try:
            folders=[]
            files=[]
            for i in os.listdir(path):
                if i.startswith( (".","_") ): continue
                if os.path.isdir( os.path.join(path,i) ):
                    folders.append( (i,os.path.join(path,i)) )
                elif i.lower().endswith( tuple(EXTS) ):
                    files.append( (i, os.path.join(path,i)) )

            olist=Tag.div(_style="overflow-x:hidden")
            for i,fp in sorted(folders):
                olist+= Tag.div( f"📂 {i}",path=fp,_onclick=lambda o: self.cb(o.path), _class="folder")
            for i,fp in sorted(files):
                olist+= Tag.div( i,path=fp,_onclick=lambda o: self.cb(o.path), _class="file")

            self.omain.clear()
            self.omain+= Tag.div(f"<b>{path}</b>")
            if path != "/":
                self.omain+= Tag.div( "📂 ..",path=os.path.dirname(path),_onclick=lambda o: self.cb(o.path) , _class="folder")
            self.omain+= olist
            return True
        except Exception as e:
            print(e)
            return False

