    import tornado.web
    class HandlerTornado(tornado.web.RequestHandler):
        async def get(self):
            path=urllib.parse.unquote(self.request.path)[5:]
            self.set_status(200)
            self.set_header('Content-type', "audio/mpeg")
            self.write(open(path,"rb").read())        

    app.add_handler("/get/.+", HandlerTornado )
    #=======================================================

    app.run()

